import React, { useState } from 'react';
import { supabase } from '../client';

const CreatePost = () => {
    const [post, setPost] = useState({ title: "", author: "", description: "", LVL: 0, ATK: 0, DEF: 0, icon: "" });
    const [imageUrl, setImageUrl] = useState(""); // Add imageUrl state

    const createPost = async (event) => {
        event.preventDefault();

        try {
            // Insert post data into database
            await supabase
                .from('Posts')
                .insert(post)
                .select();

            window.location = "/";
        } catch (error) {
            console.error('Error creating post:', error.message);
            // Handle error, e.g., display error message to the user
        }
    };

    const handleChange = (event) => {
        const { name, value } = event.target;
        setPost((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    const handleImageChange = async (event) => {
        const { value } = event.target;
        setPost((prev) => ({
            ...prev,
            icon: value,
        }));

        // Get the public URL of the selected icon and set it as the imageUrl
        try {
            const { data, error } = await supabase
                .storage
                .from('public') // Assuming the icons are stored in the public folder
                .getPublicUrl(value);

            if (error) {
                throw error;
            }

            setImageUrl(data.publicUrl);
        } catch (error) {
            console.error('Error getting icon URL:', error.message);
            // Handle error, e.g., display error message to the user
        }
    };

    return (
        <div>
            <form onSubmit={createPost}>
                {/* Your existing form inputs */}
                <label htmlFor="icon">Choose Icon:</label>
                <select id="icon" name="icon" onChange={handleImageChange}>
                    <option value="">Select an icon</option>
                    <option value="mario.png">Hero</option>
                    <option value="Lu.jpg">Support</option>
                    <option value="wario.jpg">Villain</option>
                    <option value="waluigi.png">Odd Ball</option>
                </select>
                <br />
                <br />

                {/* Display the image preview */}
                {imageUrl && (
                    <div>
                        <img
                            src={imageUrl}
                            alt="Icon"
                            style={{ width: "100px", height: "100px" }}
                        />
                    </div>
                )}

                <input type="submit" value="Submit" />
            </form>
        </div>
    );
};

export default CreatePost;
