import React, { useState } from 'react';
import { supabase } from '../client';
import Card from './Card'; // Import the Card component

const CreatePost = () => {
    const [post, setPost] = useState({ title: "", author: "", description: "", LVL: 0, ATK: 0, DEF: 0, icon: "" });

    const createPost = async (event) => {
        event.preventDefault();

        try {
            // Insert post data into database
            await supabase
                .from('Posts')
                .insert(post)
                .select();

            window.location = "/";
        } catch (error) {
            console.error('Error creating post:', error.message);
            // Handle error, e.g., display error message to the user
        }
    };

    const handleChange = (event) => {
        const { name, value } = event.target;
        setPost((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    const handleImageChange = (event) => {
        const { value } = event.target;
        setPost((prev) => ({
            ...prev,
            icon: value,
        }));
    };

    return (
        <div>
            <form onSubmit={createPost}>
                {/* Your form inputs for title, author, description, etc. */}

                <label htmlFor="icon">Choose Icon:</label>
                <select id="icon" name="icon" onChange={handleImageChange}>
                    <option value="">Select an icon</option>
                    <option value="mario.png">Mario</option>
                    <option value="Lu.jpg">Luigi</option>
                    <option value="wario.jpg">Wario</option>
                    <option value="waluigi.png">WaLuigi</option>
                </select>
                <br />
                <br />

                <input type="submit" value="Submit" />
            </form>

            {/* Render the Card component with the selected icon */}
            <Card
                title={post.title}
                author={post.author}
                description={post.description}
                LVL={post.LVL}
                ATK={post.ATK}
                DEF={post.DEF}
                icon={post.icon} // Pass the selected icon to the Card component
            />
        </div>
    );
};

export default CreatePost;
